import axios from 'axios';

const LoginService = {
    login: async function(email, password) {
        await axios.post(
            process.env.REACT_APP_AMERICANA_BASE_URL + process.env.REACT_APP_AMERICANA_LOGIN_URL,
            {
                username: email,
                password: password
            }
        ).then((res) => {
            const token = res.data.token;
    
            if (!token) {
                alert('There was an issue logging in. If this error persists, please contact customer service.');
                return;
            }
    
            localStorage.clear();
            localStorage.setItem( 'auth-info', JSON.stringify({
                'userToken': token,
                'authDate': new Date()
            }));
            
            return token;
        }).catch((error) => {
            return ''
        });

    }
};

export default LoginService;
