import axios from 'axios';

let authToken;

const LoginService = {
    login: async function(email, password) {
        await axios.post(
            process.env.REACT_APP_AMERICANA_BASE_URL + process.env.REACT_APP_AMERICANA_LOGIN_URL,
            {
                username: email,
                password: password
            }
        ).then((res) => {
            authToken = res.data.token;
            
            localStorage.clear();
            localStorage.setItem( 'auth-info', JSON.stringify({
                'userToken': authToken,
                'authDate': new Date()
            }));
            
            return authToken;
        }).catch((error) => {
            console.log(error)
            return;
        });
    }
};

export default LoginService;
