import axios from 'axios';

let authToken;

const LoginService = {
    login: async function(email, password) {
        const response = await axios.post(
            process.env.REACT_APP_AMERICANA_BASE_URL + process.env.REACT_APP_AMERICANA_LOGIN_URL,
            {
                username: email,
                password: password
            }
        ).catch((error) => {
            console.log(error)
            return '';
        });

        authToken = response.data.token;
        
        localStorage.clear();
        localStorage.setItem( 'auth-info', JSON.stringify({
            'userToken': authToken,
            'authDate': new Date()
        }));
        
        return authToken;
    }
};

export default LoginService;
